import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,

  TouchableOpacity,
  NavigatingContainer,
  ImageBackground,
} from "react-native";
import {
  connectSearchBox} from "react-instantsearch-dom";

import { InstantSearch, SearchBox, Hits } from "react-instantsearch-dom";

// TODO: CONFIGURE THE ICONS WHEN DEPLOYING: https://github.com/oblador/react-native-vector-icons
import { Button } from "react-native-elements";
import Icon from "react-native-vector-icons/FontAwesome";
import { Dimensions, PixelRatio } from "react-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import { Image } from "react-native";
import { Card, ListItem} from "react-native-elements";
import { getDownloadURL, getStorage } from "firebase/storage";
import { ref as refS } from "firebase/storage";
import * as ImagePicker from "expo-image-picker";
import { Touchable } from "react-native-web";
// import { createNativeStackNavigator } from "@react-navigation/native-stack";
import algoliasearch from "algoliasearch";


// const Stack = createNativeStackNavigator();

export default function adScreen({ navigation }) {
  const [url, setUrl] = React.useState();
  const [titleLabel, onChangetitle] = React.useState(null);
  const [image, setImage] = React.useState(
    require("../upload-photo.jpeg")
  ); // Stores the user picked Image
  var cou = 0;
    const ALGOLIA_ID = "2RC54JEYQ4";

    const Search_ID = "213f763bef6a347d940a29db2e8e2f42";

    const searchClient = algoliasearch(ALGOLIA_ID, Search_ID);

  const didMount = React.useRef(false);

//   React.useEffect(() => {
//     const func = async () => {
//       const storage = getStorage();
//       const reference = refS(storage, "/image_sample.png");
//       await getDownloadURL(reference).then((x) => {
//         setUrl(x);
//       });
//     };
//     func();
//   }, []);

//   console.log(url, cou++);

  const [search, updateSearch] = React.useState();
  console.log(search);

  const getData = async () => {
    try {
      const value = await AsyncStorage.getItem("test");
      if (value !== null) {
        console.log("value from home: ", value);
      }
    } catch (e) {
      // error reading value
    }
  };
  getData();

  const pickImage = async () => {
    // No permissions request is necessary for launching the image library

    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage(result.uri);
    }
  };


  React.useEffect(() => {
    if (!didMount.current) {
      didMount.current = true;
      return;
    }

    if (url == undefined) {
      console.log(url, "!URL 1");
      document.title = "Threshold of over 1 reached.";
    } else {
      console.log(url, "!URL 3");
      global.users = [
        {
          name: "brynn",
          avatar: url,
        },
      ];

      document.title = "No threshold reached.";
    }
  }, [url]);

  function make_square_elms (value) {
    const screenWidth = Dimensions.get('window').width;
    const screenHeight = Dimensions.get('window').height;
    if (screenWidth > screenHeight) {
      console.log(screenWidth, screenHeight, "Choosing WUDTH")
      return wp(value)
    }

    else if (screenHeight > screenWidth) {
      console.log(screenWidth, screenHeight, "Choosing HEGHT");
      return hp(value);
    }

  }
    const SearchBox = ({ currentRefinement, refine }) => {
      const [search, updateSearch] = React.useState();
      const screenWidth = Dimensions.get("window").width;
      const screenHeight = Dimensions.get("window").height;

      if (screenWidth > screenHeight) {
        return (
          <Header
            // containerStyle={styles.HeaderStyle}
            elevated={true}
            // containerStyle={styles.container}

            leftComponent={
              <View style={{ flex: 1, flexDirection: "row" }}>
                <Text
                  style={{
                    // fontSize: "1.5rem"
                    fontSize: hp("4"),
                    fontWeight: "bold",
                    textAlign: "center",
                    justifyContent: "center",
                    alignItems: "center",
                    color: "white",
                  }}
                >
                  ClothShare
                </Text>

                <TouchableOpacity
                  paddingTop={hp("2%")}
                  title="Sell"
                  color="purple"
                  onPress={() => navigation.navigate("adScreen")}
                  style={{
                    width: wp("13%"),
                    height: hp("5%"),
                    marginTop: hp("2%"),
                    justifyContent: "center",
                    borderRadius: 25,
                    backgroundColor: "rgb(90,210,138)",
                  }}
                >
                  <Text style={styles.roundButton13}>Sell or Rent</Text>
                </TouchableOpacity>

                <SearchBar
                  round={true}
                  clearIcon={true}
                  lightTheme={true}
                  placeholderTextColor={"black"}
                  containerStyle={{
                    color: "white",
                    width: wp("50"),
                    paddingLeft: 30,
                    justifyContent: "center",
                    alignItems: "center",
                    alignSelf: "center",
                    backgroundColor: "transparent",
                    borderBottomColor: "transparent",
                    borderTopColor: "transparent",
                    borderTopWidth: 0,
                    borderBottomWidth: 0,
                  }}
                  inputContainerStyle={styles.containerSearch2}
                  inputStyle={{ color: "black" }}
                  placeholder="Type Here..."
                  value={currentRefinement}
                  onChange={(event) => refine(event.currentTarget.value)}
                ></SearchBar>
              </View>
            }
            rightComponent={{ icon: "home", color: "#fff" }}
          />
        );
      } else if (screenHeight > screenWidth) {
        return (
          <Header
            // containerStyle={styles.HeaderStyle}
            elevated={true}
            // containerStyle={styles.container}

            centerComponent={
              <View style={{ flex: 1, flexDirection: "colum" }}>
                <Text style={styles.paragraph}>ClothShare</Text>

                <View
                  style={{
                    flex: 1,
                    flexDirection: "row",
                    justifyContent: "center",
                  }}
                >
                  <TouchableOpacity
                    title="Sell"
                    color="purple"
                    onPress={() => navigation.navigate("adScreen")}
                    style={{
                      width: wp("13%"),
                      height: hp("5%"),
                      justifyContent: "center",
                      alignItems: "center",
                      borderRadius: 25,
                      backgroundColor: "rgb(90,210,138)",
                    }}
                  >
                    <Text
                      style={{
                        paddingLeft: 10,
                        fontSize: hp("1.5"),
                        alignSelf: "right",
                        justifyContent: "right",
                        alignItems: "right",
                        color: "black",
                      }}
                    >
                      Sell or Rent
                    </Text>
                  </TouchableOpacity>
                  <SearchBar
                    round={true}
                    clearIcon={true}
                    lightTheme={true}
                    placeholderTextColor={"black"}
                    containerStyle={{
                      // height: "10%",
                      color: "white",
                      width: wp("85"),
                      paddingTop: 0,
                      justifyContent: "center",
                      alignItems: "center",
                      alignSelf: "center",
                      backgroundColor: "transparent",
                      borderBottomColor: "transparent",
                      borderTopColor: "transparent",
                      borderTopWidth: 0, //works
                      borderBottomWidth: 0, //works
                    }}
                    inputContainerStyle={{
                      color: "black",
                    }}
                    inputStyle={{ color: "black" }}
                    placeholder="Type Here..."
                    value={currentRefinement}
                    onChange={(event) => refine(event.currentTarget.value)}
                  ></SearchBar>
                </View>
              </View>
            }
            rightComponent={{ icon: "home", color: "#fff" }}
          />
        );
      }
    };

    const CustomSearchBox = connectSearchBox(SearchBox);
  return (
    <View style={styles.container}>
      {/* How to create a button in react native */}
      <InstantSearch indexName="SearchCloth" searchClient={searchClient}>
        {" "}
        <CustomSearchBox></CustomSearchBox>
      </InstantSearch>

      <View
        style={{ width: 200, height: hp("2%"), alignItems: "center" }}
      ></View>
      <View style={{ alignItems: "center", justifyContent: "center" }}>
        {image && (
          <Image
            source={{ uri: image }}
            style={{
              width: make_square_elms("36%"),
              height: make_square_elms("36%"),
              resizeMode: "center",
            }}
          />
        )}

        <View style={{ width: 200, height: 19 }}></View>
        <TouchableOpacity
          title="Pick an image from camera roll"
          style={styles.roundButton2}
          fontColor="white"
          onPress={pickImage}
        >
          <Text style={styles.roundButton13}>
            Pick an image from camera roll
          </Text>
        </TouchableOpacity>
      </View>

      <TextInput
        style={styles.input}
        onChangeText={onChangetitle}
        defaultValue={titleLabel}
        editable={true}
        placeholder="Enter a title: "
        selectTextOnFocus={true}
      />

      {/* <TouchableOpacity
        title="Pick an image from camera roll"
        style={styles.roundButton2}
        fontColor="white"

      >
        <Text style={styles.roundButton13}>Next</Text>
      </TouchableOpacity> */}

      <Button
        title="NEXT"
        raised={true}
        titleStyle={{ paddingLeft: 20 }}
        style={{ width: wp("50%"), height: hp("10%"), marginLeft: wp("25%") }}
        onPress={() =>
          navigation.navigate("adScreen2", {
            titleLabel: titleLabel,
            image: image,
          })
        }
        icon={
          <Icon
            name="arrow-right"
            size={(wp("4%"), hp("4%"))}
            color="white"
            iconStyle={{ paddingRight: 20 }}
          />
        }
      />

      {/* <Stack.Navigator>
