import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  NavigatingContainer,
  ImageBackground,
} from "react-native";
import { connectSearchBox } from "react-instantsearch-dom";
import algoliasearch from "algoliasearch";
import { InstantSearch, SearchBox, Hits } from "react-instantsearch-dom";
// TODO: CONFIGURE THE ICONS WHEN DEPLOYING: https://github.com/oblador/react-native-vector-icons
import { Button } from "react-native-elements";
import Icon from "react-native-vector-icons/FontAwesome";
import { Dimensions, PixelRatio } from "react-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import SelectList from "react-native-dropdown-select-list";

import AsyncStorage from "@react-native-async-storage/async-storage";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import { Image } from "react-native";
import { Card, ListItem } from "react-native-elements";
import { getDownloadURL, getStorage } from "firebase/storage";
import { ref as refS } from "firebase/storage";
import * as ImagePicker from "expo-image-picker";
import { Touchable } from "react-native-web";
// import { createNativeStackNavigator } from "@react-navigation/native-stack";

// const Stack = createNativeStackNavigator();

export default function adScreen2({ navigation }) {

  const [url, setUrl] = React.useState(null);
  const [selected, setSelected] = React.useState("");
  const [descriptionLabel, descriptionChange] = React.useState("");
  const data1 = [
    { key: "1", value: "Business Attire" },
    { key: "2", value: "Casual Wear" },
    { key: "3", value: "Formal Wear" },
    { key: "4", value: "Sportswear" },
  ];

  const data2 = [
    { key: "1", value: "Used Fair" },

    { key: "2", value: "Normal Wear" },
    { key: "3", value: "Brand new" },
  ];

  var cou = 0;

  const didMount = React.useRef(false);

  //   React.useEffect(() => {
  //     const func = async () => {
  //       const storage = getStorage();
  //       const reference = refS(storage, "/image_sample.png");
  //       await getDownloadURL(reference).then((x) => {
  //         setUrl(x);
  //       });
  //     };
  //     func();
  //   }, []);

  //   console.log(url, cou++);

  const [search, updateSearch] = React.useState();
  console.log(search);
  const data = [{ key: "1", value: "Jammu & Kashmir" }];

  const getData = async () => {
    try {
      const value = await AsyncStorage.getItem("test");
      if (value !== null) {
        console.log("value from home: ", value);
      }
    } catch (e) {
      // error reading value
    }
  };
  getData();

  React.useEffect(() => {
    if (!didMount.current) {
      didMount.current = true;
      return;
    }

    if (url == undefined) {
      console.log(url, "!URL 1");
      document.title = "Threshold of over 1 reached.";
    } else {
      console.log(url, "!URL 3");
      global.users = [
        {
          name: "brynn",
          avatar: url,
        },
      ];

      document.title = "No threshold reached.";
    }
  },  [url]);

  function make_square_elms(value) {
    const screenWidth = Dimensions.get("window").width;
    const screenHeight = Dimensions.get("window").height;
    if (screenWidth > screenHeight) {
      console.log(screenWidth, screenHeight, "Choosing WUDTH");
      return wp(value);
    } else if (screenHeight > screenWidth) {
      console.log(screenWidth, screenHeight, "Choosing HEGHT");
      return hp(value);
    }
  }

      const SearchBox = ({ currentRefinement, refine }) => {
        const [search, updateSearch] = React.useState();
        const screenWidth = Dimensions.get("window").width;
        const screenHeight = Dimensions.get("window").height;

        if (screenWidth > screenHeight) {
          return (
            <Header
              // containerStyle={styles.HeaderStyle}
              elevated={true}
              // containerStyle={styles.container}

              leftComponent={
                <View style={{ flex: 1, flexDirection: "row" }}>
                  <Text
                    style={{
                      // fontSize: "1.5rem"
                      fontSize: hp("4"),
                      fontWeight: "bold",
                      textAlign: "center",
                      justifyContent: "center",
                      alignItems: "center",
                      color: "white",
                    }}
                  >
                    ClothShare
                  </Text>

                  <TouchableOpacity
                    paddingTop={hp("2%")}
                    title="Sell"
                    color="purple"
                    onPress={() => navigation.navigate("adScreen")}
                    style={{
                      width: wp("13%"),
                      height: hp("5%"),
                      marginTop: hp("2%"),
                      justifyContent: "center",
                      borderRadius: 25,
                      backgroundColor: "rgb(90,210,138)",
                    }}
                  >
                    <Text style={styles.roundButton13}>Sell or Rent</Text>
                  </TouchableOpacity>

                  <SearchBar
                    round={true}
                    clearIcon={true}
                    lightTheme={true}
                    placeholderTextColor={"black"}
                    containerStyle={{
                      color: "white",
                      width: wp("50"),
                      paddingLeft: 30,
                      justifyContent: "center",
                      alignItems: "center",
                      alignSelf: "center",
                      backgroundColor: "transparent",
                      borderBottomColor: "transparent",
                      borderTopColor: "transparent",
                      borderTopWidth: 0,
                      borderBottomWidth: 0,
                    }}
                    inputContainerStyle={styles.containerSearch2}
                    inputStyle={{ color: "black" }}
                    placeholder="Type Here..."
                    value={currentRefinement}
                    onChange={(event) => refine(event.currentTarget.value)}
                  ></SearchBar>
                </View>
              }
              rightComponent={{ icon: "home", color: "#fff" }}
            />
          );
        } else if (screenHeight > screenWidth) {
          return (
            <Header
              // containerStyle={styles.HeaderStyle}
              elevated={true}
              // containerStyle={styles.container}

              centerComponent={
                <View style={{ flex: 1, flexDirection: "colum" }}>
                  <Text style={styles.paragraph}>ClothShare</Text>

                  <View
                    style={{
                      flex: 1,
                      flexDirection: "row",
                      justifyContent: "center",
                    }}
                  >
                    <TouchableOpacity
                      title="Sell"
                      color="purple"
                      onPress={() => navigation.navigate("adScreen")}
                      style={{
                        width: wp("13%"),
                        height: hp("5%"),
                        justifyContent: "center",
                        alignItems: "center",
                        borderRadius: 25,
                        backgroundColor: "rgb(90,210,138)",
                      }}
                    >
                      <Text
                        style={{
                          paddingLeft: 10,
                          fontSize: hp("1.5"),
                          alignSelf: "right",
                          justifyContent: "right",
                          alignItems: "right",
                          color: "black",
                        }}
                      >
                        Sell or Rent
                      </Text>
                    </TouchableOpacity>
                    <SearchBar
                      round={true}
                      clearIcon={true}
                      lightTheme={true}
                      placeholderTextColor={"black"}
                      containerStyle={{
                        // height: "10%",
                        color: "white",
                        width: wp("85"),
                        paddingTop: 0,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                        backgroundColor: "transparent",
                        borderBottomColor: "transparent",
                        borderTopColor: "transparent",
                        borderTopWidth: 0, //works
                        borderBottomWidth: 0, //works
                      }}
                      inputContainerStyle={{
                        color: "black",
                      }}
                      inputStyle={{ color: "black" }}
                      placeholder="Type Here..."
                      value={currentRefinement}
                      onChange={(event) => refine(event.currentTarget.value)}
                    ></SearchBar>
                  </View>
                </View>
              }
              rightComponent={{ icon: "home", color: "#fff" }}
            />
          );
        }
      };

      const CustomSearchBox = connectSearchBox(SearchBox);

  return (
    <View style={styles.container}>
      <InstantSearch indexName="SearchCloth" searchClient={searchClient}>
        {" "}
        <CustomSearchBox></CustomSearchBox>
      </InstantSearch>
      <View style={{ height: hp("5%") }}></View>
      {/* How to create a button i n react native */}
      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          marginLeft: wp("1"),
          paddingBottom: hp("3%"),
        }}
      >
        {" "}
        Category:{" "}
      </Text>
      <SelectList
        setSelected={setSelected}
        data={data1}
        boxStyles={{ width: wp("40%") }}
        placeholder="Select a Category"
        // dropdownStyles={{ marginHorizontal: wp("20%"), width: wp("20") }}
        // onSelect={() => alert(selected)}
      />

      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingTop: hp("3%"),
          paddingBottom: hp("3%"),
        }}
      >
        Condition:
      </Text>
      <SelectList
        setSelected={setSelected}
        data={data2}
        boxStyles={{ width: wp("40%") }}
        placeholder="Select a Condition"
        // dropdownStyles={{ marginHorizontal: wp("20%"), width: wp("20") }}
        // onSelect={() => alert(selected)}
      />
      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingTop: hp("3%"),
          paddingBottom: hp("3%"),
        }}
      >
        Description:
      </Text>
      <TextInput
        style={styles.input}
        onChangeText={descriptionChange}
        multiline={true}
        placeholder="Tell us more about the product:"
        placeholderTextColor="black"
      />

      <View style={{ height: hp("5%") }}></View>

      <Button
        title="NEXT"
        raised={true}
        titleStyle={{ paddingLeft: 20 }}
        onPress={() =>
          navigation.navigate("adScreen3", {
            titleLabel: navigation.getParam("titleLabel"),
            image: navigation.getParam("image"),
            descriptionLabel: descriptionLabel,
            selectedCondition: selected,
          })
        }
        icon={
          <Icon
            name="arrow-right"
            size={(wp("4%"), hp("4%"))}
            color="white"
            iconStyle={{ paddingRight: 20 }}
          />
        }
      />
    </View>
  );
}

const styles = StyleSheet.create({
